USE BIBLIOTECH;

-- TABELA DE CONSULTA COM OS DADOS DISPONÍVEIS QUE SERÃO USADOS PARA ALIMENTAR O BANCO.
-- DADOS QUE ESTARÃO DISPONÍVEIS NO BIBLIOTECH.

CREATE TABLE CATEGORIA(
CAT_ID INT PRIMARY KEY AUTO_INCREMENT,
CAT_NOME VARCHAR(50)
);

CREATE TABLE SUBCATEGORIA(
SCA_ID INT PRIMARY KEY AUTO_INCREMENT,
SCA_NOME VARCHAR(50),
SCA_CATEGORIA INT,
FOREIGN KEY (SCA_CATEGORIA) REFERENCES CATEGORIA (CAT_ID)
);

CREATE TABLE NACIONALIDADE(
NAC_ID INT PRIMARY KEY AUTO_INCREMENT NOT NULL,
NAC_NOME VARCHAR(100)
);

CREATE TABLE IDIOMA(
IDI_ID INT PRIMARY KEY AUTO_INCREMENT,
IDI_NOME VARCHAR(100)
);






